CREATE TABLE AMLcase (
    CASE_ID         VARCHAR(100) PRIMARY KEY,
    customer        VARCHAR(255) NOT NULL,
    create_date     DATETIME NOT NULL,
    AAA_Status      VARCHAR(30),
    CASE_Status     VARCHAR(50),
    Event_Country   VARCHAR(100),
    Analyst         VARCHAR(100),
    Source_System   VARCHAR(100)
);


-- Sample Data 
/*
CASE_ID 1000008 was created in a system called SYS001. This case involves customer Nelson Shaun and Steve Morgan. 
This case was created on 01-Jan-2019 10:10 AM. Its AAA status is 'ASSESS'. 
Current case status is 'CLOSED-RISK RELEVANT'. Event country of this case is India. 
Analyst of this case is Channel Winston.
*/

;

select 
'CASE_ID '||CASE_ID||' was created in a system called '||Source_System||'. This case involves customer '||replace(customer,',',' and')||'. This case was created on '||strftime('%d-%m-%Y %I:%M:%S %p', create_date)||'. Its AAA status is '''||aaa_status||'''. Current case status is '''||case_status||'''. Event country of this case is '||event_country||'. Analyst of this case is '||analyst||'.' 
output_TEXT
from AMLcase;


select 
'Source System of CASE_ID '||CASE_ID||' is '||Source_System||'. Customer of CASE_ID '||CASE_ID||' is '||replace(customer,',',' and')||'. Creation date of CASE_ID '||CASE_ID||' is '||strftime('%d-%m-%Y %I:%M:%S %p', create_date)||'. AAA status of CASE_ID '||CASE_ID||' is '''||aaa_status||'''. Case status of CASE_ID '||CASE_ID||' is '''||case_status||'''. Event country of CASE_ID '||CASE_ID||' is '||event_country||'. Analyst of CASE_ID '||CASE_ID||' is '||analyst||'.' 
output_TEXT
from AMLcase;

